        <Button
          size="icon"
          variant="default"
          onClick={onToggle}
          data-testid="button-mine"
          className={`w-32 h-32 md:w-40 md:h-40 rounded-full relative overflow-visible ${
            isMining ? "animate-pulse-glow" : ""
          }`}
